import './App.css';
import BoardList from './BoardList';
import BoardForm from './BoardForm';
import BoardDetail from './BoardDetail';
import React, { useState } from 'react';

function App() {
  // posts를 useState로 관리합니다.
  const [posts, setPosts] = useState([
    { id: 1, title: '첫 번째 글', body: '첫 번째 글의 내용입니다.' },
    { id: 2, title: '두 번째 글', body: '두 번째 글의 내용입니다.' },
    { id: 3, title: '세 번째 글', body: '세 번째 글의 내용입니다.' }
  ]);
  const [selectedPost, setSelectedPost] = useState(null);
  const [search, setSearch] = useState('');
  const [nextId, setNextId] = useState(4); // 다음 글 id
  const [editMode, setEditMode] = useState(false);

  // 글 추가 함수
  const addPost = (title, body) => {
    setPosts([
      ...posts,
      { id: nextId, title, body }
    ]);
    setNextId(nextId + 1);
  };

  // 글 삭제 함수
  const deletePost = (id) => {
    setPosts(posts.filter(post => post.id !== id));
    if (selectedPost && selectedPost.id === id) {
      setSelectedPost(null); // 상세보기 닫기
    }
  };

  // 더블클릭 시 수정 모드 진입
  const handleDoubleClick = (post) => {
    setSelectedPost(post);
    setEditMode(true);
  };

  // 글 수정 함수 (수정 완료 후 editMode 해제)
  const editPost = (id, newTitle, newBody) => {
    const updatedPosts = posts.map(post =>
      post.id === id ? { ...post, title: newTitle, body: newBody } : post
    );
    setPosts(updatedPosts);
    setSelectedPost(
      updatedPosts.find(post => post.id === id) || null
    );
    setEditMode(false);
  };

  // 검색어가 포함된 게시글만 보여주기 (최신 글이 위로 오도록 내림차순 정렬)
  const filteredPosts = posts
    .filter(
      post =>
        post.title.includes(search) ||
        post.body.includes(search)
    )
    .sort((a, b) => b.id - a.id);

  return (
    <div className="App">
      <h1>게시판</h1>
      <input
        type="text"
        value={search}
        onChange={e => setSearch(e.target.value)}
        placeholder="검색어를 입력하세요"
        style={{ marginBottom: '16px', width: '200px' }}
      />
      <br />
      <BoardForm addPost={addPost} />
      <BoardList
        posts={filteredPosts}
        onDelete={deletePost}
        onSelect={setSelectedPost}
        selectedId={selectedPost ? selectedPost.id : null}
        onDoubleClick={handleDoubleClick}
      />
      <BoardDetail post={selectedPost} onEdit={editPost} editMode={editMode} setEditMode={setEditMode} />
    </div>
  );
}

export default App;
