import React from 'react';

function BoardList({ posts, onDelete, onSelect, selectedId, onDoubleClick }) {
  return (
    <>
      <table style={{ margin: '0 auto', borderCollapse: 'collapse', width: '80%' }}>
        <thead>
          <tr>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>번호</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>제목</th>
            <th style={{ border: '1px solid #ccc', padding: '8px' }}>삭제</th>
          </tr>
        </thead>
        <tbody>
          {posts.map(post => (
            <tr
              key={post.id}
              style={
                post.id === selectedId
                  ? { background: '#e6f7ff' }
                  : {}
              }
            >
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>{post.id}</td>
              <td
                style={{ border: '1px solid #ccc', padding: '8px', cursor: 'pointer', textDecoration: 'underline' }}
                onClick={() => onSelect(post)}
                onDoubleClick={() => onDoubleClick && onDoubleClick(post)}
              >
                {post.title}
              </td>
              <td style={{ border: '1px solid #ccc', padding: '8px', textAlign: 'center' }}>
                <button onClick={() => onDelete(post.id)}>삭제</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      {posts.length === 0 && (
        <div style={{ textAlign: 'center', marginTop: '16px', color: '#888' }}>
          게시글이 없습니다.
        </div>
      )}
    </>
  );
}

export default BoardList;
