import React, { useState, useEffect } from 'react';

function BoardDetail({ post, onEdit, editMode, setEditMode }) {
  const [title, setTitle] = useState(post ? post.title : '');
  const [body, setBody] = useState(post ? post.body : '');

  // post가 바뀔 때마다 폼 값도 바꿔줌
  useEffect(() => {
    setTitle(post ? post.title : '');
    setBody(post ? post.body : '');
    setEditMode(false);
  }, [post, setEditMode]);

  if (!post) return null;

  const handleSubmit = (e) => {
    e.preventDefault();
    if (!title.trim() || !body.trim()) return;
    onEdit(post.id, title, body);
    // setEditMode(false); // editPost에서 해제함
  };

  return (
    <div style={{ marginTop: '20px', border: '1px solid #ccc', padding: '10px' }}>
      <h2>상세보기</h2>
      {editMode ? (
        <form onSubmit={handleSubmit}>
          <input
            type="text"
            value={title}
            onChange={e => setTitle(e.target.value)}
            style={{ fontSize: '1.2em', fontWeight: 'bold' }}
          />
          <br />
          <textarea
            value={body}
            onChange={e => setBody(e.target.value)}
            rows={4}
            style={{ width: '200px', marginTop: '8px' }}
          />
          <br />
          <button type="submit">수정 완료</button>
          <button type="button" onClick={() => setEditMode(false)}>취소</button>
        </form>
      ) : (
        <>
          <div style={{ fontSize: '1.5em', fontWeight: 'bold' }}>{post.title}</div>
          <div style={{ marginTop: '10px', whiteSpace: 'pre-line' }}>{post.body}</div>
          <button onClick={() => setEditMode(true)} style={{ marginTop: '10px' }}>수정</button>
        </>
      )}
    </div>
  );
}

export default BoardDetail;
