import './App.css';
import BoardList from './BoardList';
import BoardForm from './BoardForm';
import React, { useState } from 'react';

function App() {
  // posts를 useState로 관리합니다.
  const [posts, setPosts] = useState([
    { id: 1, title: '첫 번째 글' },
    { id: 2, title: '두 번째 글' },
    { id: 3, title: '세 번째 글' }
  ]);

  // 글 추가 함수
  const addPost = (title) => {
    setPosts([
      ...posts,
      { id: posts.length + 1, title }
    ]);
  };

  // 글 삭제 함수
  const deletePost = (id) => {
    setPosts(posts.filter(post => post.id !== id));
  };

  return (
    <div className="App">
      <h1>게시판</h1>
      <BoardForm addPost={addPost} />
      <BoardList posts={posts} onDelete={deletePost} />
    </div>
  );
}

export default App;
